import { PricingSection } from "@/registry/default/blocks/pricing-01/components/pricing-section"
import { pricingTiers } from "@/registry/default/blocks/pricing-01/constants/data"

const Home = () => {
  return (
    <div className="min-h-svh bg-background text-foreground">
      <PricingSection
        title="Get unlimited access."
        subtitle="Discover the ideal plan, beginning at under $2 per week."
        tiers={pricingTiers}
      />
    </div>
  )
}

export default Home

export interface PricingFeature {
  text: string
  included: boolean
}

export interface PricingTier {
  name: string
  description: string
  price: {
    yearly: number | null
    quarterly: number | null
    suffix?: string
  }
  features: PricingFeature[]
  buttonText: string
  buttonVariant?: "default" | "outline"
  buttonHref: string
  highlighted?: boolean
  savings?: number
}

export interface PricingProps {
  title: string
  subtitle: string
  tiers: PricingTier[]
}
